// n8n环境中直接使用测试数据，避免文件系统访问问题
// 品牌名称列表
const testBrands = [
  'HaloQuip',
  'hqpmade',
  'hqpelectric',
  'hqpfactory',
  'TestBrand1'
];

console.log('使用测试品牌数据:', testBrands);

return testBrands.map(name => ({
  json: { name: name }
}));

// 合并检查结果
// 获取当前处理的品牌名称
const brandName = $input.first().json.name;

// 获取所有输入数据
const allInputs = $input.all();
let domainResult = null;
let youtubeResult = null;

// 查找域名和YouTube检查结果
for (const input of allInputs) {
  if (input.json && input.json.Answer) {
    domainResult = input;
  } else if (input.json && typeof input.json === 'string') {
    youtubeResult = input;
  }
}

// 分析域名可用性
let domainAvailable = 'unknown';
let domainStatus = 'error';

if (domainResult && domainResult.json) {
  if (domainResult.json.Status === 3) {
    domainAvailable = true;
    domainStatus = 'NXDOMAIN - 可注册';
  } else if (domainResult.json.Status === 0) {
    domainAvailable = false;
    domainStatus = 'NOERROR - 已注册';
  }
}

// 分析YouTube可用性
let youtubeAvailable = 'unknown';
let youtubeStatus = 'error';

if (youtubeResult) {
  if (youtubeResult.statusCode === 404) {
    youtubeAvailable = true;
    youtubeStatus = '404 - 用户名可用';
  } else if (youtubeResult.statusCode === 200) {
    youtubeAvailable = false;
    youtubeStatus = '200 - 用户名已占用';
  } else {
    youtubeStatus = `HTTP ${youtubeResult.statusCode || 'unknown'}`;
  }
}

const result = {
  name: brandName,
  domainAvailable: domainAvailable,
  domainStatus: domainStatus,
  youtubeAvailable: youtubeAvailable,
  youtubeStatus: youtubeStatus,
  checkTime: new Date().toISOString()
};

console.log('检查结果:', result);

return [{ json: result }];

