const allResults = items.map(item => item.json);

// CSV标题行
const headers = ['name', 'domainAvailable', 'domainStatus', 'youtubeAvailable', 'youtubeStatus', 'checkTime'];
let csvContent = headers.join(',') + '\n';

// 数据行
allResults.forEach(result => {
  const row = headers.map(header => {
    let value = result[header] || '';
    // 处理包含逗号的值
    if (typeof value === 'string' && value.includes(',')) {
      value = `"${value}"`; 
    }
    return value;
  });
  csvContent += row.join(',') + '\n';
});

console.log('生成的CSV内容:', csvContent);

const fileName = `brand_check_results_${new Date().toISOString().slice(0,19).replace(/:/g, '-')}.csv`;

return [{
  binary: {
    data: {
      data: csvContent,
      mimeType: 'text/csv',
      fileName: fileName,
      fileExtension: 'csv'
    }
  },
  json: {
    fileName: fileName,
    totalChecked: allResults.length,
    summary: `检查完成：共${allResults.length}个品牌`,
    csvContent: csvContent
  }
}];
